/**
 * @file Contains the action generators
 * @author Edward Gonzalez
 */

import * as ActionTypes from '../constants/ActionTypes';
import * as AbortReasons from '../constants/AbortReasons';

/*
 * @ignore
 */
const AbortReasonsList = Object.keys(AbortReasons);

/**
 * Executes when the card has been inserted by the user
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const cardInserted = ()=>{
	return {'type':ActionTypes.CARD_INSERTED}
}

/**
 * Executes when the card has been processed by the ATM
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const cardProcessed = ()=>{
	return {'type':ActionTypes.CARD_PROCESSED}
}

/**
 * Executes when the pin has been inserted by the user
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 * @property {String} payload The inserted pin
 */
export const pinInserted = (pin)=>{
	if (!pin || typeof(pin) !== "string"){
		throw new Error('cannot execute action pinInserted without a valid pin',pin);
	}
	return {'type':ActionTypes.PIN_INSERTED,'payload':pin};
}

/**
 * Executes when the pin processed is valid
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const pinProcessedValid = ()=>{
	return {'type':ActionTypes.PIN_PROCESSED_VALID};
}

/**
 * Executes when the pin processed is not valid
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const pinProcessedInvalid = ()=>{
	return {'type':ActionTypes.PIN_PROCESSED_INVALID};
}

/**
 * Executes when the user specifies that he wants to withdraw some money
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const withdraw = (amount)=>{
	if (!amount || typeof(amount) !== "number" || amount < 0){
		throw new Error('cannot execute action withdraw without a valid amount',amount);
	}
	return {'type':ActionTypes.WITHDRAW,'payload':amount};
}

/**
 * Executes when the withdraw was suscesful
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const withdrawSuscesful = ()=>{
	return {'type':ActionTypes.WITHDRAW_SUSCESFUL};
}

/**
 * Executes when the money is delivered
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const moneyDelivered = ()=>{
	return {'type':ActionTypes.MONEY_DELIVERED};
}

/**
 * Executes when the user takes the card and the money
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const cardAndMoneyTaken = ()=>{
	return {'type':ActionTypes.CARD_AND_MONEY_TAKEN};
}

/**
 * Executes when the ATM is done
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const finished = ()=>{
	return {'type':ActionTypes.FINISHED};
}

/**
 * Executes when the ATM aborts for some reason
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 * @property {String} payload The reason why it aborts as defined in AbortReasons
 */
export const performAbort = (reason)=>{
	if (AbortReasonsList.indexOf(reason) === -1){
		throw new Error('cannot execute action performAbort without a valid reason',reason,'from',AbortReasonsList);
	}
	return {'type':ActionTypes.PERFORM_ABORT,'payload':reason};
}

/**
 * Executes when the ATM completes the abort process
 * @return {Object} The generated action
 * @property {String} type The type constant as defined in ActionTypes
 */
export const completeAbort = ()=>{
	return {'type':ActionTypes.COMPLETE_ABORT};
}

/**
 * @file Contains the action generators
 * @author Edward Gonzalez
 */

/**
 * When the user tried too much on an invalid pin
 */
export const TOO_MANY_FAILED_PIN_ATTEMPTS = 'TOO_MANY_FAILED_PIN_ATTEMPTS';

/**
 * When the user failed to withdraw due to the withdraw limit
 */
export const WITHDRAW_LIMIT_REACHED = 'WITHDRAW_LIMIT_REACHED';

/**
 * When the user asked more than it had
 */
export const NOT_ENOUGH_FUNDS = 'NOT_ENOUGH_FUNDS';

/**
 * When the user cancels on its own
 */
export const USER_CANCELS = 'USER_CANCELS';

/**
 * @file Contains the action types constants
 * @author Edward Gonzalez
 */

/**
 * The card has been inserted by the user
 */
export const CARD_INSERTED = 'CARD_INSERTED';

/**
 * The card has been processed by the ATM
 */
export const CARD_PROCESSED = 'CARD_PROCESSED';

/**
 * The pin has been inserted by the user
 */
export const PIN_INSERTED = 'PIN_INSERTED';

/**
 * The pin is valid
 */
export const PIN_PROCESSED_VALID = 'PIN_PROCESSED_VALID';

/**
 * The pin is invalid
 */
export const PIN_PROCESSED_INVALID = 'PIN_PROCESSED_INVALID';

/**
 * withdraw some money
 */
export const WITHDRAW = 'WITHDRAW';

/**
 * withdraw was suscesful
 */
export const WITHDRAW_SUSCESFUL = 'WITHDRAW_SUSCESFUL';

/**
 * money has been delivered
 */
export const MONEY_DELIVERED = 'MONEY_DELIVERED';

/**
 * The user took his card and money
 */
export const CARD_AND_MONEY_TAKEN = 'CARD_AND_MONEY_TAKEN';

/**
 * Process finished
 */
export const FINISHED = 'FINISHED';

/**
 * Trigger an abort
 */
export const PERFORM_ABORT = 'PERFORM_ABORT';

/**
 * Abort has been completed
 */
export const COMPLETE_ABORT = 'COMPLETE_ABORT';

/**
 * @file Contains the ATM states
 * @author Edward Gonzalez
 */

/**
 * The ATM is waiting for the card
 */
export const WAITING_FOR_CARD = 'WAITING_FOR_CARD';

/**
 * The ATM is processing the card
 */
export const PROCESSING_CARD = 'PROCESSING_CARD';

/**
 * The ATM is waiting for the pin to be inserted
 */
export const WAITING_FOR_PIN = 'WAITING_FOR_PIN';

/**
 * The ATM is processing the pin
 */
export const PROCESSING_PIN = 'PROCESSING_PIN';

/**
 * The ATM is waiting for the amount to be set
 */
export const WAITING_FOR_AMOUNT = 'WAITING_FOR_AMOUNT';

/**
 * The ATM is processing the amount
 */
export const PROCESSING_AMOUNT = 'PROCESSING_AMOUNT';

/**
 * The ATM is delivering the money
 */
export const DELIVERING = 'DELIVERING';

/**
 * The ATM has delivered the money
 */
export const DELIVERED = 'DELIVERED';

/**
 * The ATM is done with the process and will restart
 */
export const DONE = 'DONE';

/**
 * The ATM is aborting
 */
export const ABORTING = 'ABORTING';

/**
 * @file Contains the configuration of the ATM
 * @author Edward Gonzalez
 */

/**
 * The maximum times you can retry to input the pin
 */
export const MAX_PIN_TRIES = 3;

/**
 * Right pin number
 */
export const RIGHT_PIN = "1234";

/**
 * The funds of the user
 */
export const FUNDS = 1200;

/**
 * The limit of the transaction size for this account
 */
export const WITHDRAW_LIMIT = 2000;

/**
 * @file Contains the main app class that controls all the application behaviour
 * @author Edward Gonzalez
 */

import React from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';

import * as actions from '../actions';
import * as controllers from '../controllers';
import * as States from '../constants/States';
import * as AbortReasons from '../constants/AbortReasons';

/** 
 * This is the main application class that controls all the components that render a specific view
 * It uses the state out of the store in order to render the view
 */
class App extends React.Component {
	static propTypes = {
		ATMState: React.PropTypes.shape({
			'state':React.PropTypes.oneOf(Object.keys(States)).isRequired,
			'pin':React.PropTypes.string,
			'pinAttempts':React.PropTypes.number.isRequired,
			'abortReason':React.PropTypes.oneOf(Object.keys(AbortReasons)),
		})
	}
	/**
	 * Render function
	 * @returns {React.Component}
	 */
	render(){
		switch (this.props.ATMState.state){
			case States.WAITING_FOR_CARD:
				return (<controllers.WaitingForCard ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.PROCESSING_CARD:
				return (<controllers.ProcessingCard ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.WAITING_FOR_PIN:
				return (<controllers.WaitingForPin ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.PROCESSING_PIN:
				return (<controllers.ProcessingPin ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.ABORTING:
				return (<controllers.Aborting ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.WAITING_FOR_AMOUNT:
				return (<controllers.WaitingForAmount ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.PROCESSING_AMOUNT:
				return (<controllers.ProcessingAmount ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.DELIVERING:
				return (<controllers.Delivering ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.DELIVERED:
				return (<controllers.Delivered ATMState={this.props.ATMState} actions={this.props.actions}/>);
			case States.DONE:
				return (<controllers.Done ATMState={this.props.ATMState} actions={this.props.actions}/>);
			default:
				return (<div>NOT IMPLEMENTED</div>);
		}
	}
}

/**
 * @ignore
 */
const mapStateToProps = (state)=>{
	return {
		ATMState: state
	}
};

/**
 * @ignore
 */
const mapDispatchToProps = (dispatch)=>{
	return {
		actions: bindActionCreators(actions, dispatch)
	}
};

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(App);

