/**
 * @file The entry point for the application, where middlewares are applied and the store is created
 * @author Edward Gonzalez
 */

import React from 'react';
import { render } from 'react-dom';
import { applyMiddleware, createStore } from 'redux';
import { Provider } from 'react-redux';
import App from './containers/App';
import reducer from './reducers';

let store;
if (process.env.NODE_ENV === 'production'){
	store = createStore(reducer);
} else {
	let createLogger = require('redux-logger');
	const logger = createLogger();
	store = createStore(
		reducer,
		applyMiddleware(logger)
	);
}

import './css/index.css';
import './css/colors.css';
import './css/text.css';
import './css/screen.css';
import './css/animations.css';

render(
	<Provider store={store}>
		<App/>
	</Provider>,
	document.getElementById('container')
);

/**
 * @file Contains the abortion reason from the ATM
 * @author Edward Gonzalez
 */

import * as ActionTypes from '../constants/ActionTypes';

/**
 * This is the reducer for the abortion reason of the application
 *
 * @param {String} state The current state, as provided by the store
 * @param {Object} action The action that is executed
 */
export default function abortReason(state = null, action){
	switch (action.type){
		case ActionTypes.PERFORM_ABORT:
			return action.payload;
		case ActionTypes.COMPLETE_ABORT:
			return null;
		default:
			return state;
	}
}

/**
 * @file Contains information about the current amount to use in the transaction
 * @author Edward Gonzalez
 */

import * as ActionTypes from '../constants/ActionTypes';

/**
 * This is the state reducer that represents the current amount that was used in the ATM
 * the value is held until the operation is over or rolled back
 *
 * @param {String} state The current state, as provided by the store
 * @param {Object} action The action that is executed
 */
export default function amount(state = null, action){
	switch (action.type){
		case ActionTypes.WITHDRAW:
			return action.payload;
		case ActionTypes.COMPLETE_ABORT:
		case ActionTypes.PERFORM_ABORT:
		case ActionTypes.FINISHED:
			return null;
		default:
			return state;
	}
}

/**
 * @file Includes all the reducers that the application needs in order to execute
 * @author Edward Gonzalez
 */

import { combineReducers } from 'redux';
import state from './state';
import pin from './pin';
import pinAttempts from './pinAttempts';
import abortReason from './abortReason';
import amount from './amount';

/**
 * This is the root reducer that will populate the store
 */
const rootReducer = combineReducers({state,pinAttempts,abortReason,pin,amount});

export default rootReducer;

/**
 * @file Contains information about the current pin to use for processing
 * @author Edward Gonzalez
 */

import * as ActionTypes from '../constants/ActionTypes';

/**
 * This is the state reducer that represents the current pin that was
 * set into the ATM and it holded only for a brief time until is valid or invalid
 *
 * @param {String} state The current state, as provided by the store
 * @param {Object} action The action that is executed
 */
export default function pin(state = null, action){
	switch (action.type){
		case ActionTypes.PIN_INSERTED:
			return action.payload;
		case ActionTypes.PIN_PROCESSED_VALID:
		case ActionTypes.PIN_PROCESSED_INVALID:
		case ActionTypes.COMPLETE_ABORT:
		case ActionTypes.PERFORM_ABORT:
			return null;
		default:
			return state;
	}
}

/**
 * @file Contains the state reducer for the ATM pin attempts
 * @author Edward Gonzalez
 */

import * as ActionTypes from '../constants/ActionTypes';

/**
 * This is the main state reducer and represents the amount of times the user
 * has attempted to put a pin in the ATM
 * The number will reset once the ATM finishes or aborts
 *
 * @param {Number} state The current state, as provided by the store
 * @param {Object} action The action that is executed
 */
export default function pinAttempts(state = 0, action){
	switch (action.type){
		case ActionTypes.PIN_PROCESSED_VALID:
		case ActionTypes.PIN_PROCESSED_INVALID:
			return state + 1;
		case ActionTypes.FINISHED:
		case ActionTypes.COMPLETE_ABORT:
			return 0;
		default:
			return state;
	}
}

/**
 * @file Contains the state reducer for the ATM current state
 * @author Edward Gonzalez
 */

import * as States from '../constants/States';
import * as ActionTypes from '../constants/ActionTypes';

/**
 * This is the main state reducer and represents the main state of the application
 * it represents in which situation the ATM is
 *
 * @param {String} state The current state, as provided by the store
 * @param {Object} action The action that is executed
 */
export default function state(state = States.WAITING_FOR_CARD, action){
	switch (action.type){
		case ActionTypes.CARD_INSERTED:
			return States.PROCESSING_CARD;
		case ActionTypes.CARD_PROCESSED:
			return States.WAITING_FOR_PIN;
		case ActionTypes.PIN_INSERTED:
			return States.PROCESSING_PIN;
		case ActionTypes.PIN_PROCESSED_VALID:
			return States.WAITING_FOR_AMOUNT;
		case ActionTypes.PIN_PROCESSED_INVALID:
			return States.WAITING_FOR_PIN;
		case ActionTypes.WITHDRAW:
			return States.PROCESSING_AMOUNT;
		case ActionTypes.WITHDRAW_SUSCESFUL:
			return States.DELIVERING;
		case ActionTypes.MONEY_DELIVERED:
			return States.DELIVERED;
		case ActionTypes.CARD_AND_MONEY_TAKEN:
			return States.DONE;
		case ActionTypes.FINISHED:
			return States.WAITING_FOR_CARD;
		case ActionTypes.PERFORM_ABORT:
			return States.ABORTING;
		case ActionTypes.COMPLETE_ABORT:
			return States.WAITING_FOR_CARD;
		default:
			return States.WAITING_FOR_CARD;
	}
}

